mapper.select:
SELECT COUNT(*)
FROM FOLLOW
WHERE USER_ID = #{userId} AND MEMBER_ID = #{memberId}

mapper.update:
    UPDATE FOLLOW
	SET STATUS = 'Y'
	WHERE USER_ID = #{userId} AND MEMBER_ID = #{memberId}

UPDATE FOLLOW
SET STATUS = 'N'
WHERE USER_ID = #{userId} AND MEMBER_ID = #{memberId}

mapper.select:
SELECT USER_ID, MEMBER_ID, STATUS
FROM FOLLOW
WHERE USER_ID = #{userId} 
AND MEMBER_ID = #{memberId}
